    {if !spiral_mode && !(has_timelapse_safe_pos) }
        {if most_used_physical_extruder_id!= curr_physical_extruder_id || timelapse_type == 1}
            M83
            G1 Z{max_layer_z + 0.4} F1200
            M400
        {endif}
