            M971 S11 C10 O0
            M1004 S5 P1  ; external shutter
        {else}
            M9711 M{timelapse_type} E{most_used_physical_extruder_id} Z{layer_z + 0.4} S11 C10 O0 T3000
        {endif}
    {endif}

    {if !spiral_mode && !(has_timelapse_safe_pos) }
        {if most_used_physical_extruder_id!= curr_physical_extruder_id || timelapse_type == 1}
            G90
            G1 Z{max_layer_z + 3.0} F1200
            G1 Y295 F30000
            G1 Y265 F18000
            M83
        {endif}
    {endif}

    M993 A3 B3 C3
M623
